import React, { useState } from 'react';
import './App.css';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      const reader = new FileReader();
      reader.onload = (e) => setPreview(e.target.result);
      reader.readAsDataURL(file);
      setResult(null); // Reset result
    }
  };

  const handleSubmit = async () => {
    if (!selectedFile) return;

    setLoading(true);
    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await fetch('http://localhost:8000/predict-image', {
        method: 'POST',
        body: formData,
      });
      const data = await response.json();
      setResult(data);
    } catch (error) {
      setResult({ error: 'Failed to get prediction' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Deepfake Detection System</h1>
        <p>Upload an image to check if it's real or fake</p>
      </header>
      <main>
        <div className="upload-section">
          <input type="file" accept="image/*" onChange={handleFileChange} />
          {preview && <img src={preview} alt="Preview" className="preview" />}
        </div>
        <button onClick={handleSubmit} disabled={!selectedFile || loading}>
          {loading ? 'Checking...' : 'Check Authenticity'}
        </button>
        {result && (
          <div className="result">
            {result.error ? (
              <p>Error: {result.error}</p>
            ) : (
              <div>
                <h2>Result: {result.prediction}</h2>
                <p>Confidence: {(1 - result.final_score).toFixed(2)}</p>
              </div>
            )}
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
